mapper.select:
SELECT COUNT(*)
FROM POST P, BOARD B
WHERE P.BOARD_ID_FK = #{board_id}
AND B.BOARD_ID = #{board_id}

mapper.insert:
	INSERT INTO POST
	VALUES(
 		  #{post_id}
 		, NULL
		, #{user_id_fk}
		, #{board_id_fk}
		, #{post_title}
		, #{post_content}
		, SYSDATE
		, 'Y'
		, #{post_group_seq}
	)

mapper.update:
UPDATE POST
SET	  POST_TITLE = #{post_title}
		, POST_CONTENT = #{post_content}
WHERE POST_ID = #{post_id}
